const sharp=require("sharp");async function thumbnailImage(base64Data,width=150,height=150){const base64Image=base64Data.replace(/^data:image\/\w+;base64,/,""),imageBuffer=Buffer.from(base64Image,"base64");try{return`data:image/png;base64,${(await sharp(imageBuffer).resize(width,height).toBuffer()).toString("base64")}`}catch(err){return console.error("Error createing image thumbnail",err),null}}module.exports={thumbnailImage:thumbnailImage};